import React from 'react';
import { useEffect } from 'react';
import { useState } from 'react';
import { useSelector } from 'react-redux';
import TopLike from './TopLike';

const FourTop = (props) => {
    const { userChoice} = props;
    // on recupere les data de notre store au niveau du post reducer
    // console.log("state posts ", posts);
    const users = useSelector((state) => state.userReducer.users);
    console.log("state user ", users);

    const [editUserLikes, setEditUserLikes] = useState(users);

    const userLikes =[]
    useEffect(() => {
        userLikes = users
            .sort(function (a, b) {
                return b.likes - a.likes;
            })
            .slice(0, 4)

        setEditUserLikes(userLikes)
    }, [userChoice])





    console.log("state user userLikes ", userLikes);

    return (
        <div className="Topfour">
            <div className="TopfourContent">
                <h3>Top 4</h3>
                <ul>
                    {users &&
                        editUserLikes
                            .map((user, index) => (
                                <li key={user._id}>
                                    <TopLike user={user} index={index} />
                                </li>
                            ))}
                </ul>
            </div>
        </div>
    );
};

export default FourTop;
